const darkSwitch=document.getElementById("darkSwitch");function initTheme(){const e=null!==localStorage.getItem("darkSwitch")&&"dark"===localStorage.getItem("darkSwitch");darkSwitch.checked=e,e?document.body.setAttribute("class","dark"):document.body.removeAttribute("class")}function resetTheme(){darkSwitch.checked?(document.body.setAttribute("class","dark-mode"),localStorage.setItem("class","dark-mode")):(document.body.removeAttribute("class"),localStorage.removeItem("darkSwitch"))}window.addEventListener("load",()=>{darkSwitch&&(initTheme(),darkSwitch.addEventListener("change",()=>{resetTheme()}))});
